})

var nut_menu = $('.menutranghu768');
var menu_t = $('.menu-768');
$(window).click(function(e) {
    if (!menu_t.is(e.target) && menu_t.has(e.target).length == 0 && !nut_menu.is(e.target) && nut_menu.has(e
            .target).length == 0) {
        $('.menu-768').hide();
    }
});
$('.btn_menu_huongdan').click(function() {
    $(this).parents('.menu_huongdan').find('.ul_sidebar').toggle();
    $(this).parents('.menu_huongdan').find('.tam_giac').toggle();
})

function iOS() {
	return [
		'iPad Simulator',
		'iPhone Simulator',
		'iPod Simulator',
		'iPad',
		'iPhone',
		'iPod'
	].includes(navigator.platform)
	// iPad on iOS 13 detection
	|| (navigator.userAgent.includes("Mac") && "ontouchend" in document)
	}
	if (iOS()) {
		$('.ios_check').each(function( index ) {
			var rl_href = $(this).attr('ios-href');
			if (rl_href != '') {
				$(this).attr('href',rl_href).removeAttr('target');
			}
		});
}
</script>

